typedef struct {
  unsigned len;
  GskXmlrpcValue *values;
} GskXmlrpcArray;

typedef struct {
  unsigned n_members;
  GskXmlrpcNamedValue *members;
} GskXmlrpcStruct;

typedef struct {
  GskXmlrpcType type;
  union
  {
    int v_int32;
    gboolean v_boolean;
    double v_double;
    char *v_string;
    gulong v_date;
    GByteArray *v_binary_data;
    GskXmlrpcStruct *v_struct;
    GskXmlrpcArray *v_array;
  } data;
} GskXmlrpcValue;

typedef struct {
  char *name;
  GskXmlrpcValue value;
} GskXmlrpcNamedValue;

typedef struct {
  unsigned magic;		/* private, must be first */
  char *method_name;
  GskXmlrpcArray *params;
  GskXmlrpcStream *xmlrpc_stream;
} GskXmlrpcRequest;

typedef struct {
  unsigned magic;		/* private, must be first */

  GskXmlrpcArray *params;
  gboolean has_fault;
  GskXmlrpcValue fault;
} GskXmlrpcResponse;

